  position: relative;
  height: 100px;
  background: $dblue;
  color: white;
  &__header {
    display: flex;
    align-items: center;
    gap: 1rem;
    .instructor {
      position: relative;
      background: #28316013;
      box-shadow: 0 8px 32px 0 $box-shadow-blue1;
      &__curve {
        position: absolute;
        top: -10px;
        left: 20px;
        width: 30%;
        height: 120%;
        border-left: 2px solid $red;
        border-radius: 50%;
        transform: rotate(-20deg);
        box-shadow: 0 8px 32px 0 $box-shadow-blue2;
      }
      &__infos {
        display: flex;
        align-items: center;
        gap: 15px;
        font-size: 14px;
        font-family: sans-serif;
      }
      &__img {
        width: 100px;
        height: 100px;
        border-radius: 50%;
        border-right: 2px solid $red;
        @media (min-width: 400px) {
          width: 150px;
          height: 150px;
        }
      }
      &__col {
        display: flex;
        flex-direction: column;
        gap: 5px;
      }
      &__info {
        display: flex;
        align-items: center;
        gap: 10px;
        & img {
          width: 20px;
          height: 20px;
        }
      }
    }
  }
}

.svgReviewicon .arrowIcon {
  filter: invert(100%) sepia(0%) saturate(7427%) hue-rotate(48deg)
    brightness(108%) contrast(99%);
}

.swiper__wrapper {
  padding: 2rem 0;
}

.swiper {
  user-select: none;
}

.quoteSvg {
  width: 25px;
  filter: invert(22%) sepia(78%) saturate(7212%) hue-rotate(358deg)
    brightness(107%) contrast(126%);
  position: absolute;
  top: 32.5px;
  @media (min-width: 500px) {
    width: 30px;
  }
}

.review {
  position: relative;
  display: flex;
  font-size: 16px;
  padding: 5px;
  &__card {
    position: absolute;
    width: 100%;
    left: 27.5px;
    padding: 3rem 0;
    display: flex;
  }
