    top: 0;
    left: -10px;
    background: #fff;
    @media (max-width: 650px) {
      width: 90%;
    }
  }
  &__text {
    user-select: none;
    span {
      font-family: cursive;
      font-size: 22.5px;
      font-weight: 600;
      color: $red;
    }
  }
  &__img {
    width: 70px;
    height: 100px;
    object-fit: cover;
    border-radius: 35px;
  }

  &__profile {
    position: absolute;
    left: 5rem;
    bottom: -2rem;
    display: flex;
    flex-direction: column;
    font-family: "Josefin Slab", serif;
    font-size: 17px;
  }
